import axios from "axios";
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../service/api';


export default function Home() {
  const [dogs, setDogs] = React.useState(null);
  const [breeds, setBreeds] = useState([]);
  const [selectedBreed, setSelectedBreed] = useState('');
  const [selectedBreedImage, setSelectedBreedImage] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    color: '',
    size: ''
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await api.get('breeds/list/all');
        // const data = await response.json();
        console.log('response', response)
        if (response) {
          setBreeds(Object.keys(response.data.message));
        } else {
          console.error('Erro ao buscar dados das raças: Resposta vazia ou inválida');
        }
      } catch (error) {
        console.error('Erro ao buscar dados das raças:', error);
      }
    };

    fetchData();
  }, []);

  const handleBreedChange = async (event) => {
    const selectedBreed = event.target.value;
    setSelectedBreed(selectedBreed);
    try {
      const response = await api.get(`breed/${selectedBreed}/images/random`);
      setSelectedBreedImage(response.data.message);
    } catch (error) {
      console.error('Erro ao buscar imagem da raça:', error);
    }
  };


  return (
  <div style={{ flex: 1, flexDirection: 'row'}}>
    <div style={{ width: '50%', height: 100,}}>
  <h2>Lista de Raças de Cachorro</h2>
          <select value={selectedBreed} onChange={handleBreedChange}>
        {breeds.map((breed, index) => (
          <option key={index} value={breed}>
            {breed}
          </option>
        ))}
      </select>
    </div>

    <div style={{ width: '50%', height: 100,}}>
    {selectedBreedImage && (
      <div>
          <img width="300" height="200" src={selectedBreedImage} alt="Imagem da raça selecionada" />
          <form>
  <label style={{ display: 'block' }}>
    Apelido:
    <input type="text" name="name" style={{ display: 'block' }} />
  </label>
  <label style={{ display: 'block' }}>
    Idade:
    <input type="text" name="age" style={{ display: 'block' }} />
  </label>
  <label style={{ display: 'block' }}>
    Cor:
    <input type="text" name="color" style={{ display: 'block' }} />
  </label>
  <label style={{ display: 'block' }}>
    Tamanho:
    <input type="text" name="size" style={{ display: 'block' }} />
  </label>
  <button type="submit">Salvar</button>
</form>
      </div>
        )}
      </div>
  </div>
  );
}
